var coffee = require('coffee-script');

var coffeePreprocessor = function(originalPath, content, done) {
  var processed = null;
  
  if (originalPath.split(".").pop() == "coffee") {
    processed = coffee.compile(content, {});
  } else {
    processed = content;
  }

  done(processed);
};

module.exports = {
  'preprocessor:coffee': coffeePreprocessor
};